import React from "react"
import axios from 'axios'

import useLocalStorage from "hooks/useLocalStorage"
/*
кастомные хуки могут содержать в себе другие хуки
*/

export default url => {
    const baseUrl = 'https://conduit.productionready.io/api'

    const [isLoading,
        setIsLoading] = React.useState(false)
    const [response,
        setResponse] = React.useState(null)
    const [error,
        setError] = React.useState(null)

    const [options, setOptions] = React.useState({})

    const [token] = useLocalStorage('token')

    const doFetch = React.useCallback((options = {}) => {
        setOptions(options)
        setIsLoading(true)
    }, [])

    //useEffect
    React.useEffect(() => {
        const requestOptions = {
            ...options,
            ...{
                headers: {
                    authorization: token ? `Token ${token}` : ''
                }
            }
        }
        if (!isLoading) {
            return
        }

        axios(baseUrl + url, requestOptions).then(res => {
            console.log(('succes', res))
            setIsLoading(false)
            setResponse(res.data)
        }).catch(error => {
            console.log('error', error)
            setIsLoading(false)
            setError(error.response.data)
        })
    }, [isLoading, options, url, token])

    return [
        {
            isLoading,
            response,
            error
        },
        doFetch
    ]
}