import React from "react"

export default(key, initialValue = '') => {

/*
* lazy initial state, т.к начальное значение объявлено с помощью функции и из нее мы получаем определенное значение
* используем если у нас есть какие-либо вычисления в initial state
*/
    const [value,
        setValue] = React.useState(() => {
        return localStorage.getItem(key) || initialValue
    })

    React.useEffect(() => {
        localStorage.setItem(key, value)
    }, [value, key])

    return [value, setValue]
}